INSERT INTO `STANDARDS` VALUES ('1280', 'CCSS.Math.Content.6.RP.A', '2', null, null, null, 'Understand ratio concepts and use ratio reasoning to solve problems.'), ('1281', 'CCSS.Math.Content.6.RP.A.1', '2', null, null, null, 'Understand the concept of a ratio and use ratio language to describe a ratio relationship between two quantities.'), ('1282', 'CCSS.Math.Content.6.RP.A.2', '2', null, null, null, 'Understand the concept of a unit rate a/b associated with a ratio a:b with b ??? 0, and use rate language in the context of a ratio relationship.'), ('1283', 'CCSS.Math.Content.6.RP.A.3', '2', null, null, null, 'Use ratio and rate reasoning to solve real-world and mathematical problems, e.g., by reasoning about tables of equivalent ratios, tape diagrams, double number line diagrams, or equations.'), ('1284', 'CCSS.Math.Content.6.RP.A.3a', '2', null, null, null, 'Make tables of equivalent ratios relating quantities with whole number measurements, find missing values in the tables, and plot the pairs of values on the coordinate plane. Use tables to compare ratios.'), ('1285', 'CCSS.Math.Content.6.RP.A.3b', '2', null, null, null, 'Solve unit rate problems including those involving unit pricing and constant speed.'), ('1286', 'CCSS.Math.Content.6.RP.A.3c', '2', null, null, null, 'Find a percent of a quantity as a rate per 100 (e.g., 30% of a quantity means 30/100 times the quantity); solve problems involving finding the whole, given a part and the percent.'), ('1287', 'CCSS.Math.Content.6.RP.A.3d', '2', null, null, null, 'Use ratio reasoning to convert measurement units; manipulate and transform units appropriately when multiplying or dividing quantities.'), ('1288', 'CCSS.Math.Content.6.SP.A', '2', null, null, null, 'Develop understanding of statistical variability.'), ('1289', 'CCSS.Math.Content.6.SP.A.1', '2', null, null, null, 'Recognize a statistical question as one that anticipates variability in the data related to the question and accounts for it in the answers.'