INSERT INTO `STANDARDS` VALUES ('1607', 'CCSS.Math.Content.HSS-CP.B.8', '2', null, null, null, '(+) Apply the general Multiplication Rule in a uniform probability model, P(A and B) = P(A)P(BA) = P(B)P(AB), and interpret the answer in terms of the model.'), ('1608', 'CCSS.Math.Content.HSS-CP.B.9', '2', null, null, null, '(+) Use permutations and combinations to compute probabilities of compound events and solve problems.'), ('1609', 'CCSS.Math.Content.HSS-IC.A', '2', null, null, null, 'Understand and evaluate random processes underlying statistical experiments'), ('1610', 'CCSS.Math.Content.HSS-IC.A.1', '2', null, null, null, 'Understand statistics as a process for making inferences about population parameters based on a random sample from that population.'), ('1611', 'CCSS.Math.Content.HSS-IC.A.2', '2', null, null, null, 'Decide if a specified model is consistent with results from a given data-generating process, e.g., using simulation.'), ('1612', 'CCSS.Math.Content.HSS-IC.B', '2', null, null, null, 'Make inferences and justify conclusions from sample surveys, experiments, and observational studies'), ('1613', 'CCSS.Math.Content.HSS-IC.B.3', '2', null, null, null, 'Recognize the purposes of and differences among sample surveys, experiments, and observational studies; explain how randomization relates to each.'), ('1614', 'CCSS.Math.Content.HSS-IC.B.4', '2', null, null, null, 'Use data from a sample survey to estimate a population mean or proportion; develop a margin of error through the use of simulation models for random sampling.'), ('1615', 'CCSS.Math.Content.HSS-IC.B.5', '2', null, null, null, 'Use data from a randomized experiment to compare two treatments; use simulations to decide if differences between parameters are significant.'), ('1616', 'CCSS.Math.Content.HSS-IC.B.6', '2', null, null, null, 'Evaluate reports based on data.'), ('1617', 'CCSS.Math.Content.HSS-ID.A', '2', null, null, null, 'Summarize, represent, and interpret data on a single count or mea